import React, { useMemo, useState } from 'react';
import Input from './components/Input';
import Select from './components/Select';
import './App.css';
import data from './data/data.json';
import Checkbox from './components/Checkbox';
import Star from './components/Star';
import Movie from './components/Movie';

function App() {

  const [input , setInput] = useState("")
  const [filters , setFilters] = useState({
    rating:[],
    genre:[]
  })
  const [showAutocomplete, setShowAutocomplete] = useState(false);



  const handleChange = (e,id,key) => {
    if(e.target.checked){
      setFilters({...filters,[key]:[...filters[key],id]})
    }else{
      setFilters({...filters,[key]:filters[key].filter((item)=>item != id)})
    }
  }

  const handleInputFocus = () => {
    setShowAutocomplete((prev)=>!prev)
  };

  const handleMovieSelect = (movieTitle) => {
    setInput(movieTitle);
    setShowAutocomplete(false);
  };



  const movies = useMemo(() => {

    let newdata = data.movies.filter((item) =>
        item?.title?.toLowerCase().includes(input.toLowerCase())
      )


    if (filters.genre.length !== 0) {
      newdata = newdata.filter((item) => filters.genre.includes(item.category) | filters.genre.includes('Any genre'))
    }

    if (filters.rating.length !== 0) {
      newdata = newdata.filter((item) => filters.rating.includes(item.rating) | filters.rating.includes('all'));
    }

    return newdata.length !== 0 ? newdata : [];
  }, [data.movies, input, filters.genre, filters.rating]);

  return (
    <div className='flex flex-col'>
      <div className='flex justify-between'>
        <div className='w-full flex flex-col gap-1'>
          <Input onFocus={handleInputFocus} type='text' placeholder='Enter movie name' value={input} onChange={(e)=>{setInput(e.target.value)}}/>
          <div className={`flex flex-col gap-2 bg-white transition-all ${movies.length == 0 && !showAutocomplete ? "max-h-0" : "border border-gray97 px-3 py-4 max-h-screen"}`}>
            {movies?.map((item)=>(
              <div key={item?.id}>
                <Movie  item={item}/>
              </div>
            ))}
          </div>
        </div>
        <div className='w-2/5'>
          <Select title='Rating'>
            <div  className="flex items-center gap-2">
                <input type="checkbox" value={'all'} id='all'  onChange={(e)=>{handleChange(e,'all','rating')}}/>
                <label className='text-medium text-sm' htmlFor={'all'}>
                  Any rating
                </label>
              </div>
            {[...Array(10)].map((_, i) => (
              <div key={i} className="flex items-center gap-2">
                <input type="checkbox" value={i+1} id={i} onChange={(e)=>{handleChange(e,i+1,'rating')}} />
                <label htmlFor={i}>
                  <Star scale={10} value={i+1} />
                </label>
              </div>
            ))}
          </Select>
        </div>
        <div className='w-2/5'>
          <Select title='Genre'>
            {data.categories?.map((item) => (
              <Checkbox checked={filters.genre.find((it)=>it === item.id)} onChange={(e)=>{handleChange(e,item.title,'genre')}} key={item.id} item={item}/>
            ))}
          </Select>
        </div>
      </div>
    </div>
  );
}

export default App;
