import React, {useState} from 'react'
import chevron from '../../assets/icons/chevron-down.png'

const Select = ({title, children}) => {
    const [isDropdown, setisDropdown] = useState(false)

    const clickHandler = () =>{
        setisDropdown((prev)=>!prev)
    }
  return (
  <>
    <div className='absolute top-0 left-0 right-0 bottom-0' onClick={()=>{setisDropdown(false)}}>

    </div>
    <div className='flex flex-col cursor-pointer w-full relative z-20' onClick={clickHandler}>
        <div className='px-3 py-4 w-full bg-white border border-gray97'>
            <div className='flex items-center justify-between gap-4' >
                <p className='text-sm text-medium '>
                    {title}
                </p>
                <img className={`w-3 h-2 ${isDropdown ? 'rotate-180' : ''} transition-all`} src={chevron} alt=""   />
            </div>

        </div>
        <div className={`flex flex-col absolute left-0 w-full min-w-max top-full mt-1  gap-1  border-gray97 transition-all overflow-hidden ${isDropdown ? "max-h-screen p-1 border" :"max-h-0"}`}>
                {children}
        </div>
    </div>
  </>

  )
}

export default Select
