import React, { useMemo, useState } from 'react';
import Input from './components/Input';
import Select from './components/Select';
import './App.css';
import data from './data/data.json';
import Checkbox from './components/Checkbox';
import Star from './components/Star';
import Movie from './components/Movie';

function App() {

  const [input , setInput] = useState("")
  // const [filters , setFilters] = useState({
  //   rating:
  // })

  const movies = useMemo(()=>{
    const newdata = input != "" ? data.movies.filter((item)=>item?.title?.toLowerCase().includes(input.toLowerCase())) : []
    if (newdata.length !== 0){
      return newdata
    }
    return []
  },[input])

  return (
    <div className='flex flex-col'>
      <div className='flex items-center justify-between'>
        <div className='w-full'>
          <Input type='text' placeholder='Enter movie name' value={input} onChange={(e)=>{setInput(e.target.value)}}/>
        </div>
        <div className='w-2/5'>
          <Select title='Rating'>
            <div  className="flex items-center gap-2">
                <input type="checkbox" value={i+1} id={0} />
              </div>
            {[...Array(10)].map((_, i) => (
              <div key={i} className="flex items-center gap-2">
                <input type="checkbox" value={i+1} id={i} />
                <label for={i}>
                  <Star scale={10} value={i+1} />
                </label>
              </div>
            ))}
          </Select>
        </div>
        <div className='w-2/5'>
          <Select title='Genre'>
            {data.categories?.map((item) => (
              <Checkbox key={item.id} item={item}/>
            ))}
          </Select>
        </div>
      </div>
      <div>
            {movies.map((item)=>(
              <Movie item={item}/>
            ))}
      </div>
    </div>
  );
}

export default App;
