import React, { useState } from 'react'
import chevron from '../../assets/icons/chevron-down.png'

const Select = ({ title, items,  }) => {
    const [isDropdown, setisDropdown] = useState(false)

    const clickHandler = () =>{
        setisDropdown((...prev)=>!prev)
        console.log(isDropdown)
    }
  return (
    <div>

        <div className='px-3 py-4 bg-white border border-gray97 relative'>
            <div className='flex items-center justify-between' onClick={clickHandler}>
                <p>
                    {title}
                </p>
                <img className='w-3 h-2' src={chevron} alt=""   />
            </div>

        </div>
    </div>

  )
}

export default Select

Select.defaultProps = {
    items:[
        {'title':'Action','id':1},
        {'title':'Drama','id':2},
        {'title':'Test','id':3},
    ]
}
