import React, { Children, useState } from 'react'
import chevron from '../../assets/icons/chevron-down.png'
import Star from '../Star'

const Select = ({ title, items, c}) => {
    const [isDropdown, setisDropdown] = useState(false)

    const clickHandler = () =>{
        setisDropdown((prev)=>!prev)
    }
  return (
    <div className='flex flex-col relative'>
        <div className='px-3 py-4 bg-white border border-gray97 max-w-max '>
            <div className='flex items-center justify-between gap-4' onClick={clickHandler}>
                <p>
                    {title}
                </p>
                <img className={`w-3 h-2 ${isDropdown ? 'rotate-180' : ''} transition-all`} src={chevron} alt=""   />
            </div>

        </div>
        <div className={`flex flex-col absolute left-0 w-full top-full  gap-1  border-gray97 transition-all overflow-hidden ${isDropdown ? "max-h-screen p-1 border" :"max-h-0"}`}>
                {/* {items?.map((item)=>(
                    <div className='flex items-center gap-1 px-2'>
                        <input type="checkbox" />
                        <p>{item?.title}</p>
                    </div>
                ))} */}
                {children}
                {/* <Star/> */}
        </div>
    </div>

  )
}

export default Select

Select.defaultProps = {
    items:[
        {'title':'Any genre','id':1},
        {'title':'Action','id':2},
        {'title':'Comedy','id':3},
        {'title':'Drama','id':3},
        {'title':'Thriller','id':3},
    ]
}
