import React, { useMemo, useState } from 'react';
import Input from './components/Input';
import Select from './components/Select';
import './App.css';
import data from './data/data.json';
import Checkbox from './components/Checkbox';
import Star from './components/Star';
import Movie from './components/Movie';

function App() {

  const [input , setInput] = useState("")
  const [filters , setFilters] = useState({
    rating:[],
    genre:[]
  })


  const handleChange = (e,id,key) => {
    if(e.target.checked){
      setFilters({...filters,[key]:[...filters[key],id]})
    }else{
      setFilters({...filters,[key]:filters[key].filter((item)=>item != id)})
    }
  }

  const handelAll = (e,id,key)=>{
    console.log('test')
    if(e.target.checked){
      setFilters({...filters,[key]:[id]})
    }else{
      setFilters({...filters,[key]:[]})
    }
  }

  console.log(filters)


  const movies = useMemo(() => {
    let newdata = []

    if (input !== ""){
      newdata = data.movies.filter((item) =>
        item?.title?.toLowerCase().includes(input.toLowerCase())
      );
    }

    if (filters.genre.length !== 0) {
      newdata = newdata.filter((item) => filters.genre.includes(item.category))
    }

    if (filters.rating.length !== 0) {
      newdata = newdata.filter((item) => filters.rating.includes(item.rating));
    }

    return newdata.length !== 0 ? newdata : [];
  }, [data.movies, input, filters.genre, filters.rating]);

  return (
    <div className='flex flex-col'>
      <div className='flex items-center justify-between'>
        <div className='w-full'>
          <Input type='text' placeholder='Enter movie name' value={input} onChange={(e)=>{setInput(e.target.value)}}/>
        </div>
        <div className='w-2/5'>
          <Select title='Rating'>
            <div  className="flex items-center gap-2">
                <input type="checkbox" value={'all'} id='all'  onChange={(e)=>{handelAll(e,'all','rating')}}/>
                <label className='text-medium text-sm' htmlFor={'all'}>
                  Any rating
                </label>
              </div>
            {[...Array(10)].map((_, i) => (
              <div key={i} className="flex items-center gap-2">
                <input checked={filters.rating.find((it)=>it === i+1)} type="checkbox" value={i+1} id={i} onChange={(e)=>{handleChange(e,i+1,'rating')}} />
                <label htmlFor={i}>
                  <Star scale={10} value={i+1} />
                </label>
              </div>
            ))}
          </Select>
        </div>
        <div className='w-2/5'>
          <Select title='Genre'>
            {data.categories?.map((item) => (
              <Checkbox checked={filters.genre.find((it)=>it === item.id)} onChange={(e)=>{handleChange(e,item.title,'genre')}} key={item.id} item={item}/>
            ))}
          </Select>
        </div>
      </div>
      <div>
            {movies.map((item)=>(
              <Movie item={item}/>
            ))}
      </div>
    </div>
  );
}

export default App;
